#include<stdio.h>// Either do this or prototype from within the int main// (As on Line 10 of this program)// void message();int main( ) {  int i=3;  printf ( "\nAddress of i = %p", &i ) ; // %p For pointer in Hexadecimal form  printf ( "\nValue of i = %d", i ) ;  printf ( "\nValue of i with pointer notation = %d", *(&i) ) ;  i=4;  int *j;  j= (&i) ;  j = i;  // j = &i;  // *j = 6;  printf("\n %d",i);  void message();  message();  return 0;}void message() {  int i=7;  int *j,**k;  j = &i;  k = &j;  printf ( "\nAddress of i = %u", (unsigned int) &i ) ;  printf ( "\nAddress of i = %u", (unsigned int) j ) ;  printf ( "\nAddress of i = %p", j ) ;  printf ( "\nAddress of j = %p", &j ) ;  printf ("\nValue of j = %p", j ) ;  printf ( "\nValue of i = %d", i ) ;  printf ("\nValue of i = %d", *( &i ) ) ;  printf ("\nValue of i = %d", *j ) ;  printf ("\nAddress of k = %p",&k);  printf ("\nAddress of j = %p",k);  printf ("\nValue of i= %d",**k);  printf ("\nValue of j= %d",*k);}